import java.util.*;
import java.lang.*;
import java.io.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        ArrayList<Integer> rr = new ArrayList<>();

        while (true) {
            String n = sc.next();
            if (n.equals("0")) {
                break;
            }
            int r = 0;
            int t = 0;
            for (char c : n.toCharArray()) {
                if (c == 'R') {
                    t += 1;
                    if (t == 4) {
                        r += 1;
                        t = 0;
                    }
                } else {
                    t -= 1;
                    if (t == -4) {
                        t = 0;
                    }
                }
            }
            rr.add(r);
        }

        for (int r : rr) {
            System.out.println(r);
        }
    }
}

